import React, { Component } from 'react'
import axios from 'axios'

export default class Authentification extends Component {

    constructor(props) {
        super(props);

        this.state = {
            email: '',
            passWord: "",
            redirect: ""

        }
        this.change = this.change.bind(this)

    }
    setAuthToken(token) {
        if (token) {
            console.log("if token");

            var t = "bearer " + token
            console.log(t);

            axios.defaults.headers.common['authorization'] = t
        } else {
            console.log("else token");
            delete axios.defaults.headers.common['authorization'];


        }
    }

    change = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    renderRedirect1 = () => {
        if (localStorage.getItem('token')) {
            this.props.history.push("/Dashboard")
            // return <Redirect to='/Dashboard' />
        }
    }
    renderRedirect = () => {
        if (this.state.redirect) {
            // return <Redirect to='/Dashboard' />
            this.props.history.push("/Dashboard")
        }
    }
    redirect() {
        console.log("hahah");

        this.setState({
            redirect: true
        })

    }

    render() {
        return (
            <div className="container">
                <div className="row">
                    <div className="col-md-6">
                        <div class="haha">

                            <div id="login">
                                <h3 class="text-center text-white pt-5">Login form</h3>
                                <div class="container">
                                    <div id="login-row" class="row justify-content-center align-items-center">
                                        <div id="login-column" class="col-md-6">
                                            <div id="login-box" class="col-md-12">
                                                <form id="login-form" class="form" action="" method="post">
                                                    <h3 class="text-center text-info">Enregistrement</h3>
                                                    <div class="form-group">
                                                        <label for="username" class="text-info">Nom:</label><br />
                                                        <input type="text" required name="nom" id="username" class="form-control" />
                                                    </div>
                                                    <div class="form-group">
                                                        <label for="username" class="text-info">Prenom:</label><br />
                                                        <input type="text" name="prenom" id="username" class="form-control" />
                                                    </div>
                                                    <div class="form-group">
                                                        <label for="username" class="text-info">Email:</label><br />
                                                        <input type="text" name="email" id="name" class="form-control" />
                                                    </div>
                                                    <div class="form-group">
                                                        <label for="username" class="text-info">Specialite:</label><br />
                                                        <input type="text" name="Specialite" id="name" class="form-control" />
                                                    </div>
                                                    <div class="form-group">
                                                        <label for="password" class="text-info">Password:</label><br />
                                                        <input type="password" name="passWord" id="password" class="form-control" />
                                                    </div>
                                                    <div class="form-group">

                                                        <input type="submit" name="submit" class="btn btn-info btn-md" value="submit" />
                                                    </div>
                                                    <div id="register-link" class="text-right">

                                                    </div>
                                                </form>
                                                <div id="erreur" style={{ color: "#f3671f", fontSize: "2em" }}></div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="col-md-6">
                    <div >
                    <div id="login" style={{height:800}}>
                        <h3 class="text-center text-white pt-5">Se connecter</h3>
                        <div class="container">
                            <div id="login-row" class="row justify-content-center align-items-center">
                                <div id="login-column" class="col-md-6">
                                    <div id="login-box" class="col-md-12">
                                        <form id="login-form" class="form" action="" method="post">
                                            <h3 class="text-center text-info">Se connecter</h3>
                                            <div class="form-group">
                                                <label for="username" class="text-info">Nom:</label><br />
                                                <input type="text" name="email" value={this.state.email} onChange={this.change} id="username" class="form-control" />
                                            </div>
                                            <div class="form-group">
                                                <label for="password" class="text-info">Mot de passe:</label><br />
                                                <input type="password" name="passWord" value={this.state.passWord} onChange={this.change} id="password" class="form-control" />
                                            </div>
                                            <div class="form-group">

                                                <input type="submit" name="Se connecter" class="btn btn-info btn-md" value="submit"
                                                    onClick={
                                                        (e) => {
                                                            e.preventDefault()
                                                            this.login({
                                                                email: this.state.email,
                                                                passWord: this.state.passWord
                                                            })
                                                        }
                                                    } />
                                            </div>
                                            <div id="err" style={{color:"#f3671f",fontSize:"2em"}}>

                                            </div>


                                        </form>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>

                    </div>
                </div>
            </div>
        )
    }
}
